ble\").on(\"mousedown\", \"tr\", function(event) {\r\n                    \$('.selected').removeClass(\"selected\");\r\n                    var dt = \$('#garage_cards_table').DataTable();\r\n                    var index = dt.row(this).index();\r\n                    dt.row(index).select(index);\r\n                });\r\n                \r\n                \$('#garage_cards_table').DataTable().on('select', function ( e, dt, type, indexes ) {\r\n                    if (type === 'row') {\r\n                        \$('#contacts').popover('hide');\r\n                        \$(\".tab_toolbar button.blueB\").removeClass(\"disabled\");\r\n                    }\r\n                } );\r\n                \r\n                \$('#garage_cards_table').DataTable().on('deselect', function ( e, dt, type, indexes ) {\r\n                    if (type === 'row') {\r\n                        \$(\".tab_toolbar button.blueB\").addClass(\"disabled\");\r\n                    }\r\n                });\r\n            }\r\n            \r\n            \$(document).ready(function () {\r\n                init();\r\n            });\r\n            \r\n            function updateRows(){\r\n                var table = \$('#garage_cards_table').DataTable();\r\n                var p = table.rows({ page: 'current' }).nodes();\r\n                for (var k = 0; k < p.length; k++){\r\n                    var index = table.row(p[k]).index();\r\n                    table.cell(index, 8).data('<button onclick=\"transfer_action('+parseInt(table.cell(index, 0).data().split(\"-\")[1])+')\" type=\"button\" class=\"btn btn-xs btn-info\" style=\"width:100%\">Show/Edit</button>');\r\n\r\n                    if(table.cell(index, 7).data()==0){\r\n                        table.cell(index, 9).data('<button onclick=\"submit_transfer('+parseInt(table.cell(index, 0).data().split(\"-\")[1])+')\" type=\"button\" class=\"btn btn-xs btn-warning\" style=\"width:100%\">Confirm Transfer</button>');\r\n                    }els